import React, {Component} from 'react'

class Form extends Component{
    constructor(props)
    {
        super(props)

        this.state = {
            email: '',
            password: ''
        }
    }
    changed = (event)=>{
        let element = event.target.className;
        switch(element)
        {
            case "email":
            {
                this.setState({
                    email: event.target.value
                })
                break;
            }
            case "password":
            {
                this.setState({
                    password: event.target.value
                })
                break;
            }
        }
    }
    doitnow = (event)=>{
        alert("email:" + this.state.email+" \npassword: "+this.state.password)
        event.preventDefault();
        this.setState({
            email: '',
            password: ''
        })
    }
    render()
    {
        return(
            <form>
                <label>Email: </label>
                <input className="email" type="text" value={this.state.email} onChange={this.changed}></input><br></br>
                <label>Email: </label>
                <input className="password"  type="text" value={this.state.password} onChange={this.changed}></input><br></br>
                <button onClick={this.doitnow}>Submit</button>
            </form>
        )
    }
}
export default Form;
