import React, {Component} from 'react'
import './Form.css'
class Form extends Component{
    constructor(props)
    {
        super(props)

        this.state = {
            email: '',
            password: ''
        }
        this.errorRef = React.createRef();
        this.submitRef = React.createRef();
    }
    changed = (event)=>{
        let element = event.target.className;
        switch(element)
        {
            case "email":
            {
                this.setState({
                    email: event.target.value
                })
                break;
            }
            case "password":
            {
                this.setState({
                    password: event.target.value
                })
                break;
            }
            default: break;
        }
        if(this.state.email !== "" && this.state.password !== "")
        {
            this.submitRef.current.disabled = false;
        }
    }
    submit = (event)=>{
        console.log("Hey THere");
        event.preventDefault();
        alert("email:" + this.state.email+" \npassword: "+this.state.password);
        if(this.state.email !== "Rajat" && this.state.password !== "12345678")
        {
            this.errorRef.current.style.visibility = "visible";
        }
        this.setState({
            email: '',
            password: ''
        })
    }
    render()
    {
        return(
            <form>
                <div className="error" ref={this.errorRef}>*Email or Password is incorrect. Try Again*</div>
                <div>
                    <label>Email - Id  </label>
                    <input className="email" type="text" value={this.state.email} onChange={this.changed} required></input>
                </div>
                <div>
                    <label>Password </label>
                    <input className="password"  type="password" value={this.state.password} onChange={this.changed}></input>
                </div>
                <button className="submit-button" onClick={this.submit} ref={this.submitRef} disabled required>LOGIN</button>
            </form>
        )
    }
}
export default Form;
