import React, {Component} from 'react'

class Form extends Component{
    constructor(props)
    {
        super(props)

        this.state = {
            email: '',
            password: ''
        }
    }
    changed = (event)=>{
        let element = event.target.className;
        this.setState({
            element: event.target.value
            }
        )
    }
    doitnow = ()=>{
        alert("email:" + this.state.email+" \npassword: "+this.state.password)
    }
    render()
    {
        return(
            <form>
                <label>Email: </label>
                <input className="email" type="text" value={this.state.email} onChange={this.changed}></input>
                <label>Email: </label>
                <input className="password"  type="text" value={this.state.password} onChange={this.changed}></input>
                <button onclick={this.doitnow}>Submit</button>
            </form>
        )
    }
}
export default Form;
