import React, {Component} from 'react'
import './Form.css'
class Form extends Component{
    constructor(props)
    {
        super(props)

        this.state = {
            email: '',
            password: ''
        }
    }
    changed = (event)=>{
        let element = event.target.className;
        switch(element)
        {
            case "email":
            {
                this.setState({
                    email: event.target.value
                })
                break;
            }
            case "password":
            {
                this.setState({
                    password: event.target.value
                })
                break;
            }
            default: break;
        }
    }
    doitnow = (event)=>{
        event.preventDefault();
        if(this.state.email==='' && this.state.password === '')
        {
            alert("Abey input to krle bsdk");
        }
        else{
            alert("email:" + this.state.email+" \npassword: "+this.state.password);
            this.setState({
                email: '',
                password: ''
            })
        }
    }
    render()
    {
        return(
            <form>
                <div className="error">Email or Password is incorrect</div>
                <div>
                    <label>Email - Id  </label>
                    <input className="email" type="email" value={this.state.email} onChange={this.changed}></input>
                </div>
                <div>
                    <label>Password </label>
                    <input className="password"  type="password" value={this.state.password} onChange={this.changed}></input>
                </div>
                <button className="submit-button" onClick={this.doitnow}>LOGIN</button>
            </form>
        )
    }
}
export default Form;
